import logo from './logo.svg';
import './App.css';

function App() {

  const cardInfo = [
    {
      title: "СОВЕРШЕНСТВУЙТЕ ВАШ ОБРАЗ",
      p1: 'с кремом "ВЗБИТЫЕ СЛИВКИ"',
      p2: "Приобретайте крем в наборе и получите скидку прямо сейчас",
      href: "/products/view/61f5295264350f2c4c58c7ef",
      img:"https://numee-kazakhstan.s3.eu-central-1.amazonaws.com/blogImages/cropped_2.jpg",
      bg:"#cfbaf0"
    },
    {
      title: "СОВЕРШЕНСТВУЙТЕ ВАШ ОБРАЗ",
      p1: 'с кремом "ЛАСТИК ДЛЯ МОРЩИН"',
      p2: "Приобретайте крем в наборе и получите скидку прямо сейчас",
      href: "/products/view/61fb6fbcd349d2fe6d346447",
      img:"https://numee-kazakhstan.s3.eu-central-1.amazonaws.com/blogImages/cropped_1.png",
      bg:"#d9ed92"
    },
  ];

  return (
    <Carousel style={{ height: "auto" }}>
    {cardInfo.map((info, index)=> {
      return CarouselCard({info:info, windowSize: props.windowSize, key: index});
    })}
    </Carousel>
  );
  }

  const CarouselCard = (props) => { 
    const [itemHeight, setItemHeight] = useState();
    const [windowSize, setWindowSize] = useState(props.windowSize);
    const [colHeight, setColHeight] = useState();
    const adjustHeight = () => {
      switch (true) {
        case window.innerWidth <= 770 && itemHeight != { height: "80vh" }:
          setItemHeight({ height: "80vh" });
          setColHeight({ margin: "0", padding: "0", display: "inline-flex", height: "50%" });
          break;
        case window.innerWidth > 770 && itemHeight != { height: "35vw" }:
          setItemHeight({ height: "35vw" });
          setColHeight({ margin: "0", padding: "0", display: "inline-flex", height: "100%" });
          break;
      }
    };


    if(windowSize != props.windowSize){
      setWindowSize(props.windowSize);
      adjustHeight();
    }

    useEffect(() => {
      if (!itemHeight) {
        adjustHeight();
      }
    }, []);
    return (
      <Carousel.Item key={props.key} style={itemHeight}>
        <Row className="w-100" style={{ margin: "0", height: "100%" }}>
          <Col md={"6"} xs={"12"} style={colHeight}>
            <img
              className=""
              src={props.info.img}
              alt="First slide"
              style={{
                display: "inline-block",
                marginTop: "auto",
                marginBottom: "auto",
                objectFit: "contain",
                width: "100%",
              }}
            />
          </Col>
          <Card
            md={"6"}
            xs={"12"}
            as={Col}
            className="text-center"
            style={{
              display: "inline-flex",
              flexDirection: "row",
              border: "0",
              backgroundColor: props.info.bg,
              placeContent: "center flex-start",
              alignItems: "center"
            }}
          >
            <Card.Body>
              <h3 style={{ fontWeight: "400", fontSize: "calc(20px + 1vw)" }}>
                {props.info.title}
              </h3>
              <p
                className=""
                style={{ marginTop: "5%", fontSize: "calc(12px + 1vw)" }}
              >
                {props.info.p1}
              </p>
              <Button className="buyNow" href={props.info.href}>
                Купить
              </Button>
              <p
                style={{
                  fontWeight: "300",
                  marginTop: "4%",
                  fontSize: "calc(12px + 1vw)",
                }}
              >
                {props.info.p2}
              </p>
            </Card.Body>
          </Card>
        </Row>
      </Carousel.Item>
    );
  };

export default App;
